def email_notification(smtp_server,sender_email,password,receiver_emails,data=False):

    if not data:
        global df
        data = df

    port = 587  # For starttls

    message = """\
    Subject: [TESTING] - [Alert] BTC has dropped {:.2f}% in 1 hour

    Your alert for Crypto Relative Strength Analysis has been triggered. Please visit xxx for more informatio.
    """

    # Create a secure SSL context
    context = ssl.create_default_context()

    # Try to log in to server and send email
    try:
        server = smtplib.SMTP(smtp_server,port)
        server.ehlo() # Can be omitted
        server.starttls(context=context) # Secure the connection
        server.ehlo() # Can be omitted
        # Login
        server.login(sender_email, password)
        # Send email here
        server.sendmail(sender_email, receiver_emails, message)
    except Exception as e:
        # Print any error messages to stdout
        print(e)
    finally:
        server.quit() 


